const pdfText = $input.first().json.text || '';
if (!pdfText) {
  return { success: false, error: '未能提取PDF文本内容' };
}

return {
  success: true,
  pdfContent: pdfText,
  message: `成功提取PDF内容，共 ${pdfText.length} 字符`
};

const articleContent = $input.articleContent;

if (!articleContent) {
  return { success: false, error: '未提供文章内容' };
}

let wechatContent = articleContent;
wechatContent = wechatContent.replace(/^### (.+)$/gm, '<h3>$1</h3>');
wechatContent = wechatContent.replace(/^## (.+)$/gm, '<h2>$1</h2>');
wechatContent = wechatContent.replace(/^# (.+)$/gm, '<h1>$1</h1>');
wechatContent = wechatContent.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
wechatContent = wechatContent.replace(/^> (.+)$/gm, '<blockquote>$1</blockquote>');
wechatContent = wechatContent.replace(/^\*\*\*$/gm, '<hr>');

const titleMatch = articleContent.match(/^# (.*?)(?=>)/);
const title = titleMatch ? titleMatch[1].trim() : '论文解读';

const summaryMatch = articleContent.match(/> (.*?)(?=\*\*\*)/);
const summary = summaryMatch ? summaryMatch[1].trim() : '';

return {
  success: true,
  title: title,
  summary: summary,
  content: wechatContent,
  originalMarkdown: articleContent
};

// 合并节点提供的 mdnice 输出与图片数据
const mergedData = $input.all()[0].json;
const mdniceOutput = mergedData;

let wechatContent = '';
if (mdniceOutput.html) {
  wechatContent = mdniceOutput.html;
} else if (mdniceOutput.stdout) {
  wechatContent = mdniceOutput.stdout;
} else if (mdniceOutput.output) {
  wechatContent = mdniceOutput.output;
} else {
  throw new Error('未能获取 mdnice 转换后的 HTML 内容。HTTP 响应: ' + JSON.stringify(mdniceOutput).substring(0, 500));
}

if (!wechatContent || wechatContent.length < 100) {
  throw new Error('mdnice 转换的 HTML 内容异常短，可能转换失败。长度: ' + (wechatContent ? wechatContent.length : 0));
}

const titleMatch = wechatContent.match(/<h1[^>]*>.*?<span class="content"[^>]*>([^<]+)<\/span>/i);
const title = titleMatch ? titleMatch[1].trim() : '论文解读';
const summaryMatch = wechatContent.match(/<blockquote[^>]*>.*?<p[^>]*>([^<]+)<\/p>/is);
const summary = summaryMatch ? summaryMatch[1].trim() : '';

const imagePaths = Array.isArray(mergedData.imagePaths) ? mergedData.imagePaths : [];
const figures = Array.isArray(mergedData.figures) ? mergedData.figures : [];

return {
  title,
  summary,
  content: wechatContent,
  imagePaths,
  figures,
  debug_content_length: wechatContent.length
};

// 从合并后的数据中提取信息
const items = $input.all();

// 第一个输入应该是AI Agent输出处理的结果(包含title, summary, content)
const articleData = items[0].json;

// 第二个输入应该是上传封面到微信的结果(包含media_id)
const coverData = items[1] ? items[1].json : items[0].json;

// 提取封面 media_id
const mediaId = coverData.media_id;
if (!mediaId) {
  throw new Error('未能获取封面图 media_id');
}

// 提取文章元数据
const title = articleData.title || "论文解读";
const summary = articleData.summary || "";
const content = articleData.content;

// 验证文章内容
if (!content || content.length < 100) {
  throw new Error('文章内容异常短或为空。长度: ' + (content ? content.length : 0));
}

// 构建微信草稿文章对象
const article = {
  "title": title,                    // 文章标题(从HTML中提取)
  "author": "论文解读",              // 作者名称
  "digest": summary,                 // 文章摘要(从HTML中提取)
  "content": content,                // 文章内容(mdnice转换的HTML)
  "content_source_url": "",         // 原文链接(可选)
  "thumb_media_id": mediaId,        // 封面图片media_id
  "need_open_comment": 0,           // 是否打开评论
  "only_fans_can_comment": 0        // 是否仅粉丝可评论
};

return {
  articles: JSON.stringify([article]),
  // 调试信息
  debug_media_id: mediaId,
  debug_title: title,
  debug_summary: summary,
  debug_content_length: content.length
};

// 从HTTP Request节点获取图片提取服务的响应
const response = $input.first().json;

// 检查提取是否成功
if (!response.success) {
  const errorMsg = response.error || '未知错误';
  console.error('图片提取失败:', errorMsg);
  return [];
}

// 获取图表信息和第一页
const figures = response.figures || [];
const firstPage = response.first_page;

if (figures.length === 0) {
  console.log('未提取到任何图表');
}

console.log(`成功提取 ${figures.length} 个图表，转换为binary格式...`);

// 将每个图片的base64数据转换为n8n的binary格式
const results = [];

// 首先添加第一页（如果存在）
if (firstPage && firstPage.base64_data) {
  console.log(`添加第一页: ${firstPage.filename} (${firstPage.base64_data.length} chars)`);
  results.push({
    json: {
      type: 'first_page',
      fileName: firstPage.filename,
      page: firstPage.page
    },
    binary: {
      data: {
        data: firstPage.base64_data,
        mimeType: firstPage.mime_type || 'image/png',
        fileName: firstPage.filename,
        fileExtension: 'png'
      }
    }
  });
}

// 然后添加所有图表
for (let idx = 0; idx < figures.length; idx++) {
  const fig = figures[idx];

  if (!fig.base64_data) {
    console.error(`  [${idx + 1}/${figures.length}] 缺少 base64_data`);
    continue;
  }

  console.log(`  [${idx + 1}/${figures.length}] ${fig.filename} (${fig.base64_data.length} chars)`);

  results.push({
    json: {
      type: 'figure',
      imageIndex: idx,
      fileName: fig.filename,
      page: fig.page,
      figureIndex: fig.figure_index,
      caption: fig.caption
    },
    binary: {
      data: {
        data: fig.base64_data,
        mimeType: fig.mime_type || 'image/png',
        fileName: fig.filename,
        fileExtension: 'png'
      }
    }
  });
}

console.log(`转换完成，成功 ${results.length} 个图片（含第一页）`);
return results;

const uploads = $input.all().map(item => item.json).filter(item => item && item.url);
uploads.sort((a, b) => (a.imageIndex ?? 0) - (b.imageIndex ?? 0));
return [{ json: { uploadedImages: uploads } }];

// Merge节点将两个输入合并成一个item，所有字段都在同一个对象里
const mergedData = $input.first().json;

// 从合并后的数据中提取所有字段
const uploadedImages = Array.isArray(mergedData.uploadedImages) ? mergedData.uploadedImages : [];
let content = mergedData.content || '';
const firstPageUrl = mergedData.url || null;  // 来自"上传第一页到微信"的url字段

console.log('合并后的数据keys:', Object.keys(mergedData));
console.log('第一页图片URL:', firstPageUrl);
console.log('正文图片数量:', uploadedImages.length);

// 替换正文中的图片链接
if (uploadedImages.length && content.includes('<img')) {
  let counter = 0;
  content = content.replace(/<img[^>]*>/g, (tag) => {
    const upload = uploadedImages[counter];
    counter += 1;
    if (!upload || !upload.url) {
      return tag;
    }
    let updatedTag = tag.replace(/src="[^"]+"/, `src="${upload.url}"`);
    if (/data-src="[^"]+"/.test(updatedTag)) {
      updatedTag = updatedTag.replace(/data-src="[^"]+"/, `data-src="${upload.url}"`);
    } else {
      updatedTag = updatedTag.replace('<img', `<img data-src="${upload.url}"`);
    }
    return updatedTag;
  });
  console.log(`已替换 ${counter} 个正文图片链接`);
}

// 在summary（blockquote）后插入第一页图片
if (firstPageUrl) {
  console.log(`准备插入第一页图片: ${firstPageUrl}`);
  // 找到第一个</blockquote>标签后插入图片
  const blockquoteEndRegex = /<\/blockquote>/i;
  if (blockquoteEndRegex.test(content)) {
    const firstPageImg = `<p style="text-align: center;"><img src="${firstPageUrl}" data-src="${firstPageUrl}" style="max-width: 100%; height: auto;" /></p>`;
    content = content.replace(blockquoteEndRegex, `</blockquote>${firstPageImg}`);
    console.log('✓ 第一页图片已成功插入到summary后');
  } else {
    console.log('⚠ 警告: 未找到blockquote标签');
  }
} else {
  console.log('⚠ 警告: 未找到第一页图片URL');
}

return [{ 
  json: {
    title: mergedData.title,
    summary: mergedData.summary,
    content: content,
    uploadedImages: uploadedImages,
    debug_content_length: content.length,
    debug_first_page_inserted: !!firstPageUrl
  }
}];

// 过滤出类型为 figure 的图片
const items = $input.all();
const figureItems = items.filter(item => item.json.type === 'figure');

if (figureItems.length === 0) {
  console.log('未找到任何图表');
  return [];
}

console.log(`找到 ${figureItems.length} 个图表`);
return figureItems;

// 提取第一张图作为封面
const items = $input.all();

if (!items || items.length === 0) {
  throw new Error('没有可用的图片数据');
}

// 只返回第一张图片作为封面
return [items[0]];

// 过滤出类型为 first_page 的图片
const items = $input.all();
const firstPageItems = items.filter(item => item.json.type === 'first_page');

if (firstPageItems.length === 0) {
  console.log('未找到第一页图片');
  return [];
}

console.log(`找到第一页图片: ${firstPageItems[0].json.fileName}`);
return firstPageItems;
